import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import AboutMe from "./AboutMe";
import ContactMe from "./Contact";
import Home from "./Home";
import Projects from "./Projects";
import MySportsCal from "./Prjects/mySportsCal";
import NavBar from "./NavBar/NavBar";
import styled from "styled-components";
import ProjectSlingair from "./Prjects/projectSlingAir";
import ECommerceProject from "./Prjects/eCommerceProject";

const App = () => {
	return (
		<Router>
			<AppContainer>
				<NavBar />
				<ContentContainer>
					<Home />
					<AboutMe />
					<Projects />
					<ContactMe />
					<Routes>
  <Route path="/" element={<Home />} />
  <Route path="/about" element={<AboutMe />} />
  <Route path="/projects" element={<Projects />} />
  <Route path="/contact" element={<ContactMe />} />
  <Route path="/mySportsCal" element={<MySportsCal />} />
  <Route path="/Project%20SlingAir" element={<ProjectSlingair />} />
  <Route path="/E-Commerce%20Project" element={<ECommerceProject />} />
</Routes>
				</ContentContainer>
			</AppContainer>
		</Router>
	);
};
export default App;

const AppContainer = styled.div`
	display: flex;
	flex-direction: column;
	height: 100vh;
`;

const NavBarContainer = styled.div`
	z-index: 1;
`;

const ContentContainer = styled.div`
	flex-grow: 1;
	min-height: calc(100vh - 60px);
`;
