import { useRef, useState } from "react";
import emailjs from "@emailjs/browser";
import keys from "../keys";
import styled from "styled-components";
import { Icon } from "react-icons-kit";
import { linkedin } from "react-icons-kit/icomoon/linkedin";
import { github } from "react-icons-kit/icomoon/github";

const YOUR_SERVICE_ID = keys.YourServiceID;
const YOUR_TEMPLATE_ID = keys.YourTemplateID;
const YOUR_PUBLIC_ID = keys.YourPublicKey;

const ContactMe = () => {
  const form = useRef();
  const [isMessageSent, setMessageSent] = useState(false);
  const [formSubmitted, setFormSubmitted] = useState(false);

  const sendEmail = (e) => {
    e.preventDefault();

    if (!formSubmitted) {
      emailjs
        .sendForm(
          YOUR_SERVICE_ID,
          YOUR_TEMPLATE_ID,
          form.current,
          YOUR_PUBLIC_ID
        )
        .then(
          (result) => {
            console.log(result.text);
            console.log("message sent");
            setMessageSent(true);
            setFormSubmitted(true);
            form.current.reset();
          },
          (error) => {
            console.log(error.text);
          }
        );
    }
  };

  return (
    <StyledContactContainerWrapper id="text">
      <StyledContactContainer>
        <StyledContactForm>
          <h1>Contact Me</h1>
          {isMessageSent && (
            <div className="success-message">Message sent successfully!</div>
          )}
          <form ref={form} onSubmit={sendEmail}>
            <label>Name</label>
            <input type="text" name="user_name" placeholder="John Doe" required />
            <label>Phone Number</label>
            <input
              type="tel"
              name="user_phoneNumber"
              placeholder="123-456-7890"
              pattern="[0-9]{3}-[0-9]{3}-[0-9]{4}"
              required
            />
            <label>Email</label>
            <input type="email" placeholder="example@email.com" name="user_email" required />
            <label>Subject</label>
            <input type="text" name="subject" placeholder="email subject" required />
            <label>Message</label>
            <textarea
              name="message"
              placeholder="Your message here..."
              minLength={30}
              required
            />
            <input type="submit" value="Send" disabled={formSubmitted} />
          </form>
        </StyledContactForm>
        <StyledIconsContainer>
          <a
            href="https://www.linkedin.com/in/d-farrell/"
            target="_blank"
            rel="noopener noreferrer"
          >
            <Icon icon={linkedin} size={80} />
          </a>
          <a
            href="https://github.com/farrell-dan"
            target="_blank"
            rel="noopener noreferrer"
          >
            <Icon icon={github} size={80} />
          </a>
        </StyledIconsContainer>
      </StyledContactContainer>
    </StyledContactContainerWrapper>
  );
};

export default ContactMe;

const StyledContactContainerWrapper = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  min-height: 100vh;
`;

const StyledContactContainer = styled.div`
  display: flex;
  align-items: stretch;
  justify-content: center;
  width: 80vw;
  height: 80vh;
  background-color: white;
  border-radius: 10px;
  overflow: auto;
`;

const StyledContactForm = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  width: 70vw;
  padding: 2vw;

  h1 {
    font-size: 6vw;
  }

  .success-message {
    margin-top: 1rem;
    color: green;
    font-weight: bold;
  }

  form {
    width: 100%;
    display: flex;
    flex-direction: column;
    font-size: 2vw;
    margin: 0;

    input,
    textarea {
      width: 100%;
      height: 1.5vh;
      padding: 1vw;
    }

    textarea {
      height: 5vh;
    }

    label {
      margin-top: 1vh;
    }

    input[type="submit"] {
      margin-left: auto;
      margin-right: auto;
      margin-top: 1vh;
      height: 3vh;
      width: 50%;
      cursor: pointer;
      background: rgb(249, 105, 14);
      color: white;
      border: none;

      &:hover {
        background: rgb(249, 150, 50);
        transform: scale(1.05);
        transition: transform 0
ease;
		}
        }
    }
`;

const StyledIconsContainer = styled.div`
	display: flex;
	align-items: center;
	justify-content: center;/* Adjust as needed */

	a {
		text-decoration: none;
		color: #333; /* Adjust the color as needed *//* Adjust the margin as needed */
		transition: color 0.3s ease;
		margin-top: 2vh;
		margin-bottom: 2vh;
		margin-right: 5vw;
		margin-left: 5vw;

		&:hover {
			color: #0077b5; /* Adjust the hover color as needed */
			transform: scale(1.25);
			transition: transform 0.75s ease;
		}
	}
`;
