import { useRef, useState } from "react";
import emailjs from "@emailjs/browser";
import keys from "../keys";
import styled from "styled-components";
import { Icon } from "react-icons-kit";
import { linkedin } from "react-icons-kit/icomoon/linkedin";
import { github } from "react-icons-kit/icomoon/github";

const YOUR_SERVICE_ID = keys.YourServiceID;
const YOUR_TEMPLATE_ID = keys.YourTemplateID;
const YOUR_PUBLIC_ID = keys.YourPublicKey;

const ContactMe = () => {
	const form = useRef();
	const [isMessageSent, setMessageSent] = useState(false);
	const [formSubmitted, setFormSubmitted] = useState(false);

	const sendEmail = (e) => {
		e.preventDefault();

		if (!formSubmitted) {
			emailjs
				.sendForm(
					YOUR_SERVICE_ID,
					YOUR_TEMPLATE_ID,
					form.current,
					YOUR_PUBLIC_ID
				)
				.then(
					(result) => {
						console.log(result.text);
						console.log("message sent");
						setMessageSent(true);
						setFormSubmitted(true);
						form.current.reset();
					},
					(error) => {
						console.log(error.text);
					}
				);
		}
	};

	return (
		<StyledContactContainer>
			<StyledContactForm>
				<h1> Contact Me</h1>
				{isMessageSent && (
					<div className="success-message">Message sent successfully!</div>
				)}
				<form ref={form} onSubmit={sendEmail}>
					<label>Name</label>
					<input type="text" name="user_name" placeholder="John Doe" required />
					<label>Phone Number</label>
					<input
						type="tel"
						name="user_phoneNumber"
						placeholder="123-456-7890"
						pattern="[0-9]{3}-[0-9]{3}-[0-9]{4}"
						required
					/>
					<label>Email</label>
					<input
						type="email"
						placeholder="example@email.com"
						name="user_email"
						required
					/>
					<label>Subject</label>
					<input
						type="text"
						name="subject"
						placeholder="email subject"
						required
					/>
					<label>Message</label>
					<textarea
						name="message"
						placeholder="Your message here..."
						minLength={30}
						required
					/>
					<input type="submit" value="Send" disabled={formSubmitted} />
				</form>
			</StyledContactForm>
			<StyledIconsContainer>
				<a
					href="https://www.linkedin.com/in/d-farrell/"
					target="_blank"
					rel="noopener noreferrer"
				>
					<Icon icon={linkedin} size={100} />
				</a>
				<a
					href="https://github.com/farrell-dan"
					target="_blank"
					rel="noopener noreferrer"
				>
					<Icon icon={github} size={100}/>
				</a>
			</StyledIconsContainer>
		</StyledContactContainer>
	);
};

export default ContactMe;

const StyledContactContainer = styled.div`
	display: flex;
	justify-content: center;
	align-items: cen;
	height: 90vh;
	border: 1px solid black;
	width: 80vw;
`;

const StyledIconsContainer = styled.div`
    flex: 1;
    display: flex;
    align-items: center;
    justify-content: space-around; /* Adjust as needed */

    a {
        text-decoration: none;
        color: #333; /* Adjust the color as needed */
        margin: 0 5px; /* Adjust the margin as needed */
        transition: color 0.3s ease;

        &:hover {
            color: #0077b5; /* Adjust the hover color as needed */
            transform: scale(1.5);
            transition: transform 0.75s ease;
        }
    }
`;

const StyledContactForm = styled.div`
	flex: 1;
	display: flex;
	flex-direction: column;
	align-items: center;
	justify-content: center;

	.success-message {
		margin-top: 1rem;
		color: green;
		font-weight: bold;
	}

	form {
		width: 400px;
		display: flex;
		flex-direction: column;
		font-size: 16px;

		input,
		textarea {
			width: 100%;
			height: 35px;
			padding: 7px;
			outline: none;
			border-radius: 5px;
			border: 1px solid rgb(220, 220, 220);

			&:focus {
				border: 2px solid rgba(0, 206, 158, 1);
			}
		}

		textarea {
			max-width: 100%;
			min-width: 100%;
			max-height: 100px;
			min-height: 100px;
		}

		label {
			margin-top: 1rem;
		}

		input[type="submit"] {
			margin-top: 2rem;
			cursor: pointer;
			background: rgb(249, 105, 14);
			color: white;
			border: none;
		}
	}
`;
