import { Link } from "react-router-dom";
import { useEffect, useRef } from "react";
import styled from "styled-components";
import "./NavBar.css";

const NavBar = () => {
	const homeRef = useRef(null);
	const aboutMeRef = useRef(null);
	const projectsRef = useRef(null);
	const contactRef = useRef(null);

	const scrollToSection = (ref) => {
		ref.current.scrollIntoView({ behavior: "smooth" });
	};

	useEffect(() => {
		const primaryNav = document.querySelector(".primary-navigation");
		const navToggle = document.querySelector(".mobile-nav-toggle");

		navToggle.addEventListener("click", () => {
			const visibility = primaryNav.getAttribute("data-visible");

			if (visibility === "false") {
				primaryNav.setAttribute("data-visible", "true");
				navToggle.setAttribute("aria-expanded", "true");
			} else if (visibility === "true") {
				primaryNav.setAttribute("data-visible", "false");
				navToggle.setAttribute("aria-expanded", "false");
			}
		});
	}, []);

	var resizeTimer = window.addEventListener("resize", () => {
		document.body.classList.add("resize-animation-stopper");
		clearTimeout(resizeTimer);
		resizeTimer = setTimeout(() => {
			document.body.classList.remove("resize-animation-stopper");
		}, 400);
	});

	return (
		<div>
			<button
				className="mobile-nav-toggle"
				aria-controls="primary-navigation"
				aria-expanded="false"
			>
				<span className="sr-only"></span>
			</button>
			<StyledNav>
				<StyledUl
					id="primary-navigation"
					data-visible="false"
					className="primary-navigation flex"
				>
					<StyledLi className="active">
						<StyledLink aria-hidden="true" onClick={() => scrollToSection(homeRef)}>
							Home
						</StyledLink>
					</StyledLi>
					<StyledLi>
						<StyledLink aria-hidden="true"  onClick={() => scrollToSection(aboutMeRef)}>
							About Me
						</StyledLink>
					</StyledLi>
					<StyledLi>
						<StyledLink aria-hidden="true" onClick={() => scrollToSection(projectsRef)}>
							Projects
						</StyledLink>
					</StyledLi>
					<StyledLi>
						<StyledLink aria-hidden="true" onClick={() => scrollToSection(contactRef)}>
							Contact Me
						</StyledLink>
					</StyledLi>
				</StyledUl>
			</StyledNav>
		</div>
	);
};

export default NavBar;

const StyledNav = styled.nav`
	display: flex;
	flex-wrap: wrap;
	justify-content: flex-end;
	align-items: center;
	min-height: 5rem;
	background-color: #040a1d;
	--gap: 0;
`;

const StyledUl = styled.ul`
	display: flex;
`;

const StyledLi = styled.li`
	display: flex;
	align-items: center;
	margin-right: 2rem;

	@media (max-width: 616px) {
		margin-right: 0;
	}
`;

const StyledLink = styled(Link)`
	text-decoration: none;
	color: #c5c6c6;
	cursor: pointer;
	font-size: 1.5rem;
	font-family: "Roboto", sans-serif;

	&:hover {
		background-color: #23233b;
	}
`;
