import MyCV from "../assets/Daniel Farrell CV.jpg";
import DownloadButton from "./CVDownload";
import styled from "styled-components";

const AboutMe = () => {
	return (
		<Container>
			<h1>About Me</h1>
			<ContentContainer>
				<Column>
					<h2>Biography</h2>

				</Column>
				<RightColumn>
					<h2>My CV</h2>
					<img src={MyCV} alt="My CV" />
					<DownloadButton />
				</RightColumn>
			</ContentContainer>
		</Container>
	);
};

export default AboutMe;

const Container = styled.div`
	display: flex;
	flex-direction: column;
	align-items: center;
	width: 80vw;
	height: 80vh;
	min-width: 680px;
	margin: 20px auto;
	background-color: lightblue; /* Set the background color of the box */
	padding: 20px; /* Adjust padding as needed */
	border-radius: 5%;
	border: 1px solid #ccc; /* Add border if desired */
`;

const ContentContainer = styled.div`
	display: flex;
	justify-content: space-between;
	align-items: start;
`;

const Column = styled.div`
	width: 45%; /* Adjust column width as needed */
	text-align: center;
`;

const RightColumn = styled(Column)`
display: flex;
flex-direction: column;
justify-content: center;
align-items: center;

	img {
		max-height: 50vh;
		box-shadow: 0 0 0 5px white, 0 0 0 8px black;
	}

	button {
		display: block;
		margin: 10px auto;
	}
`;
