import styled from 'styled-components';
import { Icon } from "react-icons-kit";
import { github } from "react-icons-kit/icomoon/github";

const projects = [
	{ id: 1, title: 'Project 1', description: 'Description for Project 1', image: '../assets/project1.jpg', githubLink: 'https://github.com/user/project1' },
	{ id: 2, title: 'Project 2', description: 'Description for Project 2', image: '../assets/project2.jpg', githubLink: 'https://github.com/user/project2' },
	{ id: 3, title: 'Project 3', description: 'Description for Project 3', image: '../assets/project3.jpg', githubLink: 'https://github.com/user/project3' },
	{ id: 4, title: 'Project 4', description: 'Description for Project 4', image: '../assets/project4.jpg', githubLink: 'https://github.com/user/project4' },
	{ id: 5, title: 'Project 5', description: 'Description for Project 5', image: '../assets/project5.jpg', githubLink: 'https://github.com/user/project5' },
	{ id: 6, title: 'Project 6', description: 'Description for Project 6', image: '../assets/project6.jpg', githubLink: 'https://github.com/user/project6' },
  ];


  const Portfolio = () => {
	return (
		  <CenteredContainer>
		<PortfolioContainer>
		<Header>My Projects</Header>
        {projects.map(project => (
          <ProjectCard key={project.id}>
            <ProjectImageContainer>
              <ProjectImage src={project.image} alt={project.title} />
              <HoverContent>
                <h3>{project.title}</h3>
                <p>{project.description}</p>
                <GitHubIcon href={project.githubLink} target="_blank" rel="noopener noreferrer">
                  <Icon size={32} icon={github} />
                </GitHubIcon>
              </HoverContent>
            </ProjectImageContainer>
          </ProjectCard>
        ))}
	  </PortfolioContainer>
	  </CenteredContainer>
	);
  };

export default Portfolio;

const CenteredContainer = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
 margin-top : 5vh ;
  height: 80vh;
`;

const PortfolioContainer = styled.div`
  display: flex;
  flex-wrap: wrap;
  justify-content: space-evenly;
  align-items: center;
  background-color: #fff; /* White background */
  padding: 20px;
  height: 80vh;
  width: 80vw;
`;

const Header = styled.h1`
  width: 100%;
  text-align: center;
  margin: 0;
  font-size: 5vh;
  height: 6vh;
`;

const ProjectCard = styled.div`
  min-width: 20vw;
  min-height: 20vh;
  margin: 2vh 1vw;
  overflow: hidden;
  position: relative;
`;

const ProjectImageContainer = styled.div`
  position: relative;
`;

const ProjectImage = styled.img`
  width: 100%;
  height: 100%;
  object-fit: cover;
`;

const HoverContent = styled.div`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: rgba(255, 255, 255, 0.8);
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  opacity: 0;
  transition: opacity 0.3s ease-in-out;
  ${ProjectCard}:hover & {
    opacity: 1;
  }

  h3 {
    margin-bottom: 10px;
  }
`;

const GitHubIcon = styled.a`
  margin-top: 10px;
  text-decoration: none;
  color: #000;
  cursor: pointer;

  :hover{
	co
  }
`;
