import { useRef } from "react";
import emailjs from "@emailjs/browser";
// import keys from "../keys";
import styled from "styled-components";

// const YOUR_SERVICE_ID = keys.YourServiceID;
// const YOUR_TEMPLATE_ID = keys.YourTemplateID;
// const YOUR_PUBLIC_ID = keys.YourPublicKey;

// console.log(YOUR_SERVICE_ID);
// console.log(YOUR_TEMPLATE_ID);
// console.log(YOUR_PUBLIC_ID);

const ContactMe = () => {
	const form = useRef();

	const sendEmail = (e) => {
		e.preventDefault();

		emailjs
			.sendForm(
				"service_9gnmjhk",
				"template_4s9cj0i",
				form.current,
				"M9Q3ud9idYClrd798"
			)
			.then(
				(result) => {
					console.log(result.text);

				},
				(error) => {
					console.log(error.text);
				}
			);
	};

	return (
		<StyledContactForm>
			<h1> Contact Me</h1>
			<form ref={form} onSubmit={sendEmail}>
				<label>Name</label>
				<input type="text" name="user_name" />
				<label>Phone Number</label>
				<input type="tel" name="user_phoneNumber" />
				<label>Email</label>
				<input type="email" name="user_email" />
				<label>Subject</label>
				<input type="text" name="subject" />
				<label>Message</label>
				<textarea name="message" />
				<input type="submit" value="Send" />
			</form>
		</StyledContactForm>
	);
};

export default ContactMe;

const StyledContactForm = styled.div`
	display: flex;
    flex-direction: column;
	align-items: center;
	justify-content: center;
	height: 100vh;

	form {
		width: 400px;
		display: flex;
		flex-direction: column;
		font-size: 16px;

		input,
		textarea {
			width: 100%;
			height: 35px;
			padding: 7px;
			outline: none;
			border-radius: 5px;
			border: 1px solid rgb(220, 220, 220);

			&:focus {
				border: 2px solid rgba(0, 206, 158, 1);
			}
		}

		textarea {
			max-width: 100%;
			min-width: 100%;
			max-height: 100px;
			min-height: 100px;
		}

		label {
			margin-top: 1rem;
		}

		input[type="submit"] {
			margin-top: 2rem;
			cursor: pointer;
			background: rgb(249, 105, 14);
			color: white;
			border: none;
		}
	}
`;
