import { useState } from "react";
import styled from "styled-components";
import DanielImage1 from "../assets/IMG_2243.JPG";
import DanielImage2 from "../assets/IMG_9209.jpeg";

const Home = () => {
	const [isHovered, setIsHovered] = useState(false);

	const handleHover = () => {
		setIsHovered(!isHovered);
	};

	return (
		<Container>
			<Content>
				<TextContainer>
					<MediumText>I am</MediumText>
					<BigText>Daniel Farrell</BigText>
					<MediumText>a Full Stack Web Developer</MediumText>
					<SmallText>Scroll down to get to know me better!</SmallText>
				</TextContainer>
				<ImageContainer>
					<StyledImage
						src={isHovered ? DanielImage2 : DanielImage1}
						alt="Daniel Farrell"
						onMouseEnter={handleHover}
						onMouseLeave={handleHover}
					/>
				</ImageContainer>
			</Content>
		</Container>
	);
};

export default Home;

const Container = styled.div`
	display: flex;
	align-items: center;
	justify-content: center;
	min-height: calc(100vh - 60px); 
`;

const Content = styled.div`
	display: flex;
	width: 80%;
	background-color: #2C4268; 
	border-radius: 10px;
`;

const TextContainer = styled.div`
	flex: 3;
	display: flex;
	align-items: center;
	flex-direction: column;
	justify-content: center;
	font-family: 'Roboto', sans-serif;;
`;

const BigText = styled.h1`
	font-size: 6vw;
	margin: 0; 
	font-weight: 900;
	background: linear-gradient(#C5C6C6, #7ccdf4);
	background: -webkit-linear-gradient(#C5C6C6, #7ccdf4);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;

	color: #7ccdf4;
`;

const MediumText = styled.h2`
	font-size: 3.5vw;
	margin: 1.5vh;
	font-family: 'Archivo', sans-serif;
	font-weight: 400;
`;

const SmallText = styled.p`
	font-size: 2.25vw;
	margin: 0;
`;

const ImageContainer = styled.div`
	flex: 2;
	display: flex;
	align-items: center;
	justify-content: center;
`;

const StyledImage = styled.img`
	width: 115%; /* Adjust the width as needed */
	height: 115%; /* Adjust the height as needed */
	border-radius: 50%;
	object-fit: cover;
	transition: transform 1s ease, opacity 3s ease;
	box-shadow: 0 0 0 5px white, 0 0 0 8px black;

	&:hover {
		transform: scale(1.05);
		opacity: 0.85;
	}
`;
