import { Link } from "react-router-dom";
import { useEffect } from "react";
import styled from "styled-components";
import "./NavBar.css";



const NavBar = () => {

	useEffect(() => {
		const primaryNav = document.querySelector('.primary-navigation');
		const navToggle = document.querySelector('.mobile-nav-toggle');

		navToggle.addEventListener('click', () => {
		  const visibility = primaryNav.getAttribute('data-visible');

		  if (visibility ==="false") {
			primaryNav.setAttribute('data-visible', "true")
			navToggle.setAttribute('aria-expanded', "true")
		  } else if ( visibility === "true") {
			primaryNav.setAttribute('data-visible', "false")
			navToggle.setAttribute('aria-expanded', "false")
		  }
		});


		// return () => {
		//   navToggle.removeEventListener('click');
		// };
	  }, []);

	return (
		<>
			<button
				className="mobile-nav-toggle"
				aria-controls="primary-navigation"
				aria-expanded="false"
			>
				<span className="sr-only"></span>
			</button>
			<StyledNav>
				<StyledUl id="primary-navigation" data-visible="false" className="primary-navigation flex">
					<StyledLi className="active">
						<StyledLink aria-hidden="true" to="/">
							Home
						</StyledLink>
					</StyledLi>
					<StyledLi>
						<StyledLink aria-hidden="true" to="/about-me">
							About Me
						</StyledLink>
					</StyledLi>
					<StyledLi>
						<StyledLink aria-hidden="true" to="/projects">
							Projects
						</StyledLink>
					</StyledLi>
					<StyledLi>
						<StyledLink aria-hidden="true" to="/contact">
							Contact Me
						</StyledLink>
					</StyledLi>
				</StyledUl>
			</StyledNav>
		</>
	);
};

export default NavBar;

const StyledNav = styled.nav`
	display: flex;
	justify-content: flex-end;
	align-items: center;
	height: 8vh;
	/* background-color: #040a1d; */
`;

const StyledUl = styled.ul`
	display: flex;
`;

const StyledLi = styled.li`
	display: flex;
	align-items: center;
`;

const StyledLink = styled(Link)`
	text-decoration: none;
	color: #c5c6c6;
	cursor: pointer;
	font-size: 3vh;
	font-family: "Roboto", sans-serif;

	&:hover {
		background-color: #23233b;
	}
`;
