import { useState } from "react";
import styled from "styled-components";
import DanielImage1 from "../assets/IMG_2243.JPG";
import DanielImage2 from "../assets/IMG_9209.jpeg";

const Home = () => {
    const [isHovered, setIsHovered] = useState(false);

    const handleHover = () => {
        setIsHovered(!isHovered);
    };

    return (
        <Container>
            <Content>
                <TextContainer>
                    <h1>Daniel Farrell</h1>
                    <h2>I'm a Full Stack Web Developer</h2>
                    <p>Scroll down to get to know me a bit better!</p>
                </TextContainer>
                <ImageContainer>
                    <StyledImage
                        src={isHovered ? DanielImage2 : DanielImage1}
                        alt="Daniel Farrell"
                        onMouseEnter={handleHover}
                        onMouseLeave={handleHover}
                    />
                </ImageContainer>
            </Content>
        </Container>
    );
};

export default Home;

const Container = styled.div`
    display: flex;
    align-items: center;
    justify-content: center;
    height: 100vh;
`;

const Content = styled.div`
    display: flex;
    width: 80%;
`;

const TextContainer = styled.div`
    flex: 3;
    display: flex;
    align-items: center;
	flex-direction: column;
	justify-content: center;
`;

const ImageContainer = styled.div`
    flex: 2;
    display: flex;
    align-items: center;
    justify-content: center;
`;

const StyledImage = styled.img`
    width: 400px; /* Adjust the width as needed */
    height: 400px; /* Adjust the height as needed */
    border-radius: 50%;
    object-fit: cover;
    transition: transform 3s ease, opacity 3s ease;
    box-shadow: 0 0 20px rgba(0, 0, 0, 0.3); /* Add shadow for better visibility */

    &:hover {
        transform: scale(0.9);
        opacity: 0.7;
    }
`;
